import aboutRoute from './routes/about';
import blogRoute from './routes/blog';
import contactRoute from './routes/contact';
import cors from 'cors';
import destinationRoute from './routes/destination';
import dotenv from 'dotenv';
import express from 'express';
import extrasRoute from './routes/extras';
import footerRoute from './routes/footer';
import homeRoute from './routes/home';
import hotelRoute from './routes/hotel';
import mongoose from 'mongoose';
import testimonialRoute from './routes/testimonial';
dotenv.config();
const app = express();
app.use(express.json());
app.use(cors({ credentials: true, origin: process.env.ORIGIN_URL }));
mongoose.connect(process.env.MONGO_URL, { useNewUrlParser: true, useUnifiedTopology: true });
app.use('/api/blog', blogRoute);
app.use('/api/destination', destinationRoute);
app.use('/api/hotel', hotelRoute);
app.use('/api/footer', footerRoute);
app.use('/api/about', aboutRoute);
app.use('/api/contact', contactRoute);
app.use('/api/testimonial', testimonialRoute);
app.use('/api/extras', extrasRoute);
app.use('/api/home', homeRoute);
app.listen(process.env.PORT || 3000, () => {
    console.log("Server running on port 3000!");
});
